import { useState } from "react";
import { Button, Form, Modal } from "react-bootstrap";

export default function EditProfile({
  show,
  hideCallback,
  user,
  setAlert,
  profileData,
}) {
  const [bio, setBio] = useState(profileData.bio);
  const [firstName, setFirstName] = useState(profileData.first_name);
  const [lastName, setLastName] = useState(profileData.last_name);
  const [file, setFile] = useState("");

  function updateProfile() {
    const formData = new FormData();
    formData.append("file", file);
    formData.append("user", user);
    formData.append("first_name", firstName);
    formData.append("last_name", lastName);
    formData.append("bio", bio);
    const requestOptions = {
      method: "POST",
      body: formData,
    };
    fetch("/updateProfile", requestOptions)
      .then((res) => res.json())
      .then((data) => {
        setAlert({
          variant: "success",
          message: "Profile updated successfully!",
        });
        if (file) data.image_url = URL.createObjectURL(file);
        hideCallback(data);
      })
      .catch((err) => setAlert({ variant: "danger", message: err.message }));
  }

  return (
    <Modal show={show} onHide={hideCallback}>
      <Modal.Header>
        <Modal.Title>Edit Profile</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group className="mb-3">
            {profileData.photo && !file ? (
              <img src={profileData.photo.asset.url} className="upload-image" />
            ) : (
              <img
                src={file ? URL.createObjectURL(file) : null}
                className="upload-image"
              />
            )}
          </Form.Group>
          <Form.Group className="mb-3">
            <input
              type="file"
              accept="image/*"
              onChange={(e) => setFile(e.target.files[0])}
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Control
              type="text"
              placeholder="First Name"
              defaultValue={profileData.first_name}
              onInput={(e) => setFirstName(e.target.value)}
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Control
              type="text"
              placeholder="Last Name"
              defaultValue={profileData.last_name}
              onInput={(e) => setLastName(e.target.value)}
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Control
              type="text"
              placeholder="Bio"
              defaultValue={profileData.bio}
              onInput={(e) => setBio(e.target.value)}
            />
          </Form.Group>
          <div>
            <Button variant="primary" onClick={updateProfile}></Button>
          </div>
        </Form>
      </Modal.Body>
    </Modal>
  );
}
