import React from "react";
import { useState } from "react";
import { Form, Button } from "react-bootstrap";
import "../css/SignUp.css";

export default function SignUp({ setAlert }) {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [username, setUsername] = useState("");

  function CreateAccount(e) {
    const requestOptions = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        firstName: firstName,
        lastName: lastName,
        username: username,
      }),
    };
    fetch("/createUser", requestOptions)
      .then((res) => {
        return res.json();
      })
      .then((_data) =>
        setAlert({
          variant: "success",
          message: "Youre account has been created.",
        })
      )
      .catch((err) => console.error(err));
  }

  function updateUsername(e) {
    setUsername(e.target.value);
  }
  function updateFirstName(e) {
    setFirstName(e.target.value);
  }
  function updateLastName(e) {
    setLastName(e.target.value);
  }

  return (
    <Form className="sign-up-form">
      <Form.Group className="mb-4">
        <Form.Label>Username</Form.Label>
        <Form.Control
          type="text"
          placeholder="Username"
          onInput={updateUsername}
        />
      </Form.Group>
      <Form.Group className="mb-4">
        <Form.Label>First Name</Form.Label>
        <Form.Control
          type="text"
          placeholder="First Name"
          onInput={updateFirstName}
        />
      </Form.Group>
      <Form.Group className="mb-4">
        <Form.Label>Last Name</Form.Label>
        <Form.Control
          type="text"
          placeholder="Last Name"
          onInput={updateLastName}
        />
      </Form.Group>
      <Button variant="primary" type="button" onClick={CreateAccount}>
        Create Account
      </Button>
    </Form>
  );
}
